import React, { useState } from "react";


const App = () => {

  const startBg = "#8e44ad";
  const [bg, setBg] = useState(startBg);
  const bgChange = () => {
        setBg('#34495e');
  }
  return (
  <>
    <div style = {{ backgroundColor: bg }}>
        <button onClick = {bgChange}> Click me </button> 
    </div>
  </>
  );
};

export default App;
