  const inputEvent = (event) => {
    console.log(event.target.value)
    setName(event.target.value)
  }

  const onSubmit = () => {
    setFullName(name)
  }
  return(
    <>
      <h1> Hello {name} </h1>
      <input type = "text" placeholder = "Enter Your Name" 

      />
      <button onClick = {onSubmit}> click me </button>
    </>
  );
};

export default App;
