import React, { useState } from "react";


const App = () => {

  const startBg = "#8e44ad";
  const [bg, setBg] = useState(startBg);
  const [name, setName] = useState('Click me')
  const bgChange = () => {
        setBg('#34495e');
        setName("Ouch!! 🐺")
  }
  return (
  <>
    <div style = {{ backgroundColor: bg }}>
        <button onClick = {bgChange}> {name}> </button> 
    </div>
  </>
  );
};

export default App;
